mapper.select:
   			SELECT * FROM store_order WHERE 
   			creation_time   >= #{beginDate} and creation_time <= #{endDate} 
   			
	   			and state <>'9' 
   			
   			
   			and store_id = #{storeId}
   			
   			
   			and id = #{orderId} 
   			
   			
   			and operator_id LIKE '%${operatorId}%'   
   			
   			
   			and id IN(SELECT order_id FROM item WHERE name LIKE  '%${orderName}%' and creation_time >= #{beginDate} and creation_time <= #{endDate}  )
   			
   			
   			and state=#{state}

   		select name from (
   			select sum(amount) total ,name from item where order_id in(select id from store_order where state <> '9' and state <> '1' and store_id=#{storeId}
			and creation_time >= #{startDate} and creation_time <= #{endDate})
			group by name order by total desc limit #{top} 
			) a;
